mapper.insert:
INSERT INTO NOTICE
(	 NOTICE_NO
   , NOTICE_CATEGORY	
   , NOTICE_TITLE
   , NOTICE_CONTEXT
   , CREATE_DATE
   , MODIFY_DATE
   , STATUS
   , USER_NO
)
VALUES
(	SEQ_NNO.NEXTVAL
   , #{ notice_category }
   , #{ notice_title }
   , #{ notice_context }
   , SYSDATE
   , SYSDATE
   , DEFAULT
   , #{ user_no }
)

mapper.update:
UPDATE NOTICE SET
		NOTICE_CATEGORY	= #{ notice_category }
	   , NOTICE_TITLE =   #{ notice_title }
	   , NOTICE_CONTEXT = #{ notice_context }
	   , MODIFY_DATE = SYSDATE
	   , STATUS = DEFAULT
	   , USER_NO = #{ user_no }
	    WHERE NOTICE_NO = #{ notice_no }

	UPDATE 	NOTICE 
	SET		STATUS = 'N'
    WHERE NOTICE_NO = #{ notice_no }
