mapper.select:
SELECT PURCHASE_NO , PURCHASE_DATE , USE_POINT , PURCHASE_PRICE , PICKUP_DATE , PICKUP_PLACE , PICKUP_TIME , ORDER_STATUS , STATUS , REFUND_DATE , USER_NO , USER_NAME FROM (SELECT ROWNUM RNUM , PLIST.* FROM(SELECT P.PURCHASE_NO , P.PURCHASE_DATE , P.USE_POINT , P.PURCHASE_PRICE , P.PICKUP_DATE , P.PICKUP_PLACE , P.PICKUP_TIME , P.ORDER_STATUS , P.STATUS , P.REFUND_DATE , P.USER_NO , M.USER_NAME FROM PURCHASE P LEFT JOIN MEMBER M ON(P.USER_NO = M.USER_NO) WHERE P.STATUS = 'Y'
AND TO_DATE(TO_CHAR(P.PURCHASE_DATE, 'yy/MM/dd'), 'yy/MM/dd') >= TO_DATE(#{ startDate }, 'yy/MM/dd') AND TO_DATE(TO_CHAR(P.PURCHASE_DATE, 'yy/MM/dd'), 'yy/MM/dd') <= TO_DATE(#{ endDate }, 'yy/MM/dd')
AND P.ORDER_STATUS = #{ searchStatus }
AND P.PURCHASE_NO LIKE '%' || #{ searchValue } || '%'
AND M.USER_NAME LIKE '%' || #{ searchValue } || '%'
ORDER BY P.PURCHASE_DATE DESC) PLIST) WHERE RNUM BETWEEN #{ startRow } AND #{ endRow }

mapper.update:
UPDATE PURCHASE
   SET ORDER_STATUS = #{ orderStatus }
 WHERE PURCHASE_NO = #{ purchaseNo }

UPDATE POINT
   SET STATUS = 'N'
 WHERE PURCHASE_NO = #{ purchaseNo }

UPDATE MEMBER
   SET USER_POINT = USER_POINT + #{ usePoint }
 WHERE USER_NO = #{ userNo }

UPDATE PURCHASE
   SET ORDER_STATUS = #{ orderStatus }
 WHERE PURCHASE_NO = #{ purchaseNo }

UPDATE PURCHASE
   SET STATUS = 'N'
 WHERE PURCHASE_NO = #{ purchaseNo }
