mapper.insert:
INSERT 
	INTO PRODUCT
(
	  PRODUCT_NO
	, PRODUCT_KNAME
	, PRODUCT_ENAME
	, COST_PRICE
	, SALE_PRICE
	, PRODUCT_COUNT
	, WINERY
	, THUMBNAIL
	, CAPACITY
	, ABV
	, TASTE_GRAPH
	, INFORMATION
	, AWARD
	, TIP
	, LABEL_IMAGE
	, STATUS
	, CREATE_DATE
	, MODIFY_DATE
	, VARIETY_NO
	, TYPE_NO
	, COUNTRY_NO	
)
VALUES
(
	  SEQ_PNO.NEXTVAL
	, #{ productKName }
	, #{ productEName }
	, #{ costPrice }
	, #{ salePrice }
	, #{ productCount }
	, #{ winery }
	, #{ thumbnail }
	, #{ capacity }
	, #{ abv }
	, #{ tasteGraph }
	, #{ information }
	, #{ award }
	, #{ tip }
	, #{ labelImage }
	, DEFAULT
	, SYSDATE
	, SYSDATE
	, #{ variety.varietyNo }
	, #{ type.typeNo }
	, #{ country.countryNo }
)

mapper.update:
UPDATE PRODUCT
	SET STATUS = 'N'
WHERE PRODUCT_NO = #{ productNo }

