mapper.insert:
INSERT
		INTO PURCHASE
	(
		PURCHASE_NO
	  , PURCHASE_DATE
	  , USE_POINT
	  , PURCHASE_PRICE
	  , PICKUP_DATE
	  , PICKUP_PLACE
	  , PICKUP_TIME
	  , ORDER_STATUS
	  , STATUS
	  , REFUND_DATE
	  , USER_NO
	)
	VALUES
	(
		#{purchase_no}
	  ,	DEFAULT
	  , #{use_point}
	  , #{purchase_price}
	  , #{pickup_date}
	  , #{pickup_place}
	  , #{pickup_time}
	  , DEFAULT
	  , DEFAULT
	  , NULL
	  , #{user_no}
	)

mapper.update:
	UPDATE MEMBER
  	   SET USER_POINT = #{user_point}
  	 WHERE USER_NO = #{user_no}

