mapper.select:
SELECT daily , totalOrder , totalPrice , point , totalRefund FROM (SELECT ROWNUM RNUM , PLIST.* FROM(SELECT P1.PURCHASE_DATE daily , COUNT(P1.PURCHASE_NO) totalOrder , SUM(P1.PURCHASE_PRICE) totalPrice , SUM(P1.USE_POINT) point , (SELECT SUM(P2.PURCHASE_PRICE) - SUM(P2.USE_POINT) FROM PURCHASE P2 WHERE TO_CHAR(P2.REFUND_DATE, 'yy/MM/dd') IS NOT NULL AND TO_CHAR(P1.PURCHASE_DATE, 'yy/MM/dd') = TO_CHAR(P2.PURCHASE_DATE, 'yy/MM/dd') AND P2.ORDER_STATUS IN ('환불완료', '결제취소')) totalRefund FROM PURCHASE P1
WHERE TO_DATE(TO_CHAR(PURCHASE_DATE, 'yy/MM/dd'), 'yy/MM/dd') >= TO_DATE(#{ startDate }, 'yy/MM/dd') AND TO_DATE(TO_CHAR(PURCHASE_DATE, 'yy/MM/dd'), 'yy/MM/dd') <= TO_DATE(#{ endDate }, 'yy/MM/dd')
GROUP BY P1.PURCHASE_DATE ORDER BY P1.PURCHASE_DATE DESC) PLIST) WHERE RNUM BETWEEN #{ startRow } AND #{ endRow }
